//if.... else if ....else条件语句	
package java_201807;

public class IfElseIf {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int t=3;
		if(t==0) {														
			System.out.println("one");
		}
		else if(t==10) {
			System.out.println("two");
		}
		else if(t==20) {
			System.out.println("three");
		}
		else {
			System.out.println("end");
		}
	}

}
