//关系运算符
package java_201809;

public class GuanXi {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	     int a = 10;
	     int b = 20;
	     System.out.println("a == b = " + (a == b) );
	     System.out.println("a != b = " + (a != b) );
	     System.out.println("a > b = " + (a > b) );
	     System.out.println("a < b = " + (a < b) );
	     System.out.println("b >= a = " + (b >= a) );
	     System.out.println("b <= a = " + (b <= a) );

	}

}
