//逻辑运算符
package java_201809;

public class LuoJi {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	     boolean a = true;
	     boolean b = false;
	     System.out.println("a && b = " + (a&&b));
	     System.out.println("a || b = " + (a||b) );
	     System.out.println("!(a && b) = " + !(a && b));

	}

}



