//算术运算符
package java_201809;

public class SuanShu {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x=8,y=5;
		System.out.println("x+y="+(x+y));
		System.out.println("x-y="+(x-y));
		System.out.println("x*y="+(x*y));
		System.out.println("取整x/y="+(x/y));
		System.out.println("取余x%y="+(x%y));
	}

}
