package java_201905;

public class DataTypeTest {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        System.out.println("java.lang.Byte"); 
        System.out.println("byte二进制位数=" + Byte.SIZE); 
        System.out.println("byte最小值=" + Byte.MIN_VALUE);  
        System.out.println("byte最大值=" + Byte.MAX_VALUE);  
        System.out.println(); 
        
        System.out.println("java.lang.Integer");
        System.out.println("int二进制位数="+Integer.SIZE);
        System.out.println("int最小值="+Integer.MIN_VALUE);
        System.out.println("int最大值="+Integer.MAX_VALUE);
        System.out.println();
        
        System.out.println("java.lang.Short");
        System.out.println("short二进制位数="+Short.SIZE);
        System.out.println("short最小值="+Short.MIN_VALUE);
        System.out.println("short最大值="+Short.MAX_VALUE);
        System.out.println();
        
        System.out.println("java.lang.Long");
        System.out.println("long二进制位数="+Long.SIZE);
        System.out.println("long最小值="+Long.MIN_VALUE);
        System.out.println("long最大值="+Long.MAX_VALUE);
        System.out.println();
        
        System.out.println("java.lang.Float");
        System.out.println("float二进制位数="+Float.SIZE);
        System.out.println("float最小值="+Float.MIN_VALUE);
        System.out.println("float最大值="+Float.MAX_VALUE);
        System.out.println();
        
        System.out.println("java.lang.Double");
        System.out.println("double二进制位数="+Double.SIZE);
        System.out.println("double最小值="+Double.MIN_VALUE);
        System.out.println("double最大值="+Double.MAX_VALUE);
        System.out.println();
        
        System.out.println("java.lang.Character");
        System.out.println("char二进制位数="+Character.SIZE);
        System.out.println("char最小值="+(int)Character.MIN_VALUE);
        System.out.println("char最大值="+(int)Character.MAX_VALUE);
        System.out.println();
	}

}
